import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

//import la funcion que se encarga de buscar los datos de la API
import buscaTragos from "../funciones/buscarTragos";

const TarjetaTrago = () => {
  const [datos, setDatos] = useState([]);
  const [buscar, setBuscar] = useState(true);

  const { id } = useParams();

  useEffect(() => {
    if (buscar) {
      buscaTragos("id", id).then((res) => {
        setDatos(res);
      });
      setBuscar((prevState) => !prevState);
    }
    console.log(datos);
  }, [buscar, datos, id]);
  return (
    <div>
      <div className="card mb-3">
        <img
          className="card-img-top"
          src={datos[0].imagen}
          alt={datos[0].nombre}
        />
        <div className="card-body">
          <h5 className="card-title">{datos[0].nombre}</h5>
          <h2 className="card-title">{datos[0].tipo}</h2>
          <p className="card-text">{datos[0].instrucciones}</p>
        </div>
      </div>
    </div>
  );
};

export default TarjetaTrago;
