import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

//import la funcion que se encarga de buscar los datos de la API
import buscarTragos from "../funciones/buscarTragos";

const TarjetaTrago = ({ match }) => {
  const [datos, setDatos] = useState([]);
  const [buscar, setBuscar] = useState(true);

  useEffect(() => {
    if (buscar) {
      const { id } = useParams();
      buscaTragos("id", id).then((res) => {
        setDatos(res);
      });
      setBuscar((prevState) => !prevState);
      console.log(datos);
      console.log(id);
    }
  });

  console.log(useParams());

  return (
    <div>
      soy una tarjeta
      {id}
      <p>soy un parrafito</p>
    </div>
  );
};

export default TarjetaTrago;
