const buscaTragos = async () => {
  const tragos = await fetch(
    "https://www.thecocktaildb.com/api/json/v1/1/search.php?f=a"
  );
  const tragosJSON = await tragos.json();
  const tragosFormateado = await formatearTragos(tragosJSON.drink);
  console.log(tragosFormateado);
};

const formatearTragos = (arrayTragos) => {
  return {
    id: arrayTragos.idDrink,
    nombre: arrayTragos.strDrink,
    alcohol: arrayTragos.strAlcoholic,
  };
};

export default buscaTragos;
