import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const TarjetaTrago = ({ match }) => {
  const [datos, setDatos] = useState([]);

  const { id } = useParams();

  useEffect(() => {
    const datosJson = async (id) => {
      const datosPedidos = await fetch(
        `https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`
      );
      const datosFormato = await datosPedidos.json();
      return datosFormato;
    };

    const valor = datosJson(id);
    console.log(valor);
  }, [datos]);

  console.log(useParams());

  return (
    <div>
      soy una tarjeta
      {id}
      <p>soy un parrafito</p>
    </div>
  );
};

export default TarjetaTrago;
