import React, { useState } from "react";

//importo componentes
import Buscador from "../componentes/buscador";
import ListaTragos from "../componentes/listaTragos";

//importo otras funciones
import buscaTragos from "../interactuarAPI/buscarTragos";

/*Los datos del trago que trae el objeto son:
  idDrink: util para hacer la peticion al servidor
  strAlcoholic: si trae o no alcohol (agregar esa data)
  strCategory: la categoria del trago (ej: cocktail)
  strIngredient(n): hay n y estan listados, no son un array. Tiene cada uno un ing
  strMeasure(n): tambien listados, la cantidad de cada uno de los ing
  strInstructions: Instrucciones de armado en ENG (si se agrega una opcion de idioma, puedo poner DE) 
  
  Esto ultimo habria que formatearlo tal que:

  */

const Home = () => {
  /*El estado de buscador es la entrada que entra desde buscador
  y lo uso para pasar a la lista como tiene que filtrar lo que muestra*/
  const [buscador, setBuscador] = useState("");

  /*Aca hago el fetch de los tragos a la API y lo paso como una prop a
  la Lista de Tragos para que ahi se filtre */

  const tragos = buscaTragos();

  return (
    <div>
      <Buscador setBuscador={setBuscador} />
      <ListaTragos buscador={buscador} />
    </div>
  );
};

export default Home;
